class Solution {
    public boolean canCross(int[] stones) {
        // If the difference of the first 2 stones are not 1 then we return false;
        if(stones[1] - stones[0] > 1)
            return false;

        // We store all the stone positions in a Set
        Set<Integer> positions = new HashSet<>();
        for(int stone : stones)
            positions.add(stone);

        // We explore all the possible paths
        return canCross(stones, positions, new HashMap<>(), stones[1], 1);
    }

    boolean canCross(int[] stones, Set<Integer> positions, Map<String, Boolean> dp, int currPos, int jump){

        // If we have reached the last stone return true
        int lastStone = stones[stones.length - 1];        
        if(currPos == lastStone)
            return true;

        // If we have landed at a position which is not there in the original list then return false
        if(!positions.contains(currPos))
            return false;

        // Else we have landed in a position which is there in the stones[] and we would want to check
        // if we have already explored this path with the current jump
        String key = new StringBuilder()
            .append(currPos)
            .append("-")
            .append(jump)
            .toString();

        // If this is the first time we are exploring then we explore the all the possible paths
        if(!dp.containsKey(key)) {
            // With jump = k
            if(canCross(stones, positions, dp, currPos + jump, jump))
                dp.put(key, true);
            // With jump = k - 1, only if jump > 1
            else if(jump > 1 && canCross(stones, positions, dp, currPos + jump-1, jump-1))
                dp.put(key, true);
            // With jump = k + 1
            else
                dp.put(key, canCross(stones, positions, dp, currPos + jump+1, jump+1));
        }
        return dp.get(key);
    }
}
