// APPROACH
//      core concept here is using DP and unordered_map+unordered_set.
//      Given this input: 
//      [0,1,3,5,6,8,12,17]
//      at the end, the map will be populated as:
//      {0: [0]}
//      {1: [1]}    1+1 (1 current position, 1 because k); 1+2 (1 current position, 2 because k+1)
//                  1+1 = 2 we put at the key = 2 the step 1.
//                  1+2 = 3 we put at the key = 3 the step 2.
//      {3: [1]}    3+1; 3+2
//      {5: [2]}    5+1; 5+2; 5+3
//      {6: [1]}    6+1; 6+2
//      {8: [3, 2]} 8+2; 8+3; 8+4 - 8+1; 8+2; 8+3
//      {12: [4]}   12+3; 12+4; 12+5
//      {17: [5]}
//      we are able to reach the last stone in 5 jump!

//      Remember: at eack i-th we can jump forward of k-1; k or k+1 where 
//      k is the previous jump.

class Solution {
public:
    bool canCross(vector<int>& stones) {
        unordered_map<int, unordered_set<int>> jumpsMap;
        for (auto s: stones)
            jumpsMap[s] = {};

        jumpsMap[0].insert(0);

        for (int i = 0; i < stones.size(); ++i) {
            for (auto k: jumpsMap[stones[i]]) {
                for (int step = k - 1; step <= k + 1; ++step) {
                    auto it = jumpsMap.find(stones[i] + step);
                    if (step > 0 && it != jumpsMap.end())
                        jumpsMap[stones[i] + step].insert(step);
                }
            }
        }
        return didWeGetToTheLastStone (jumpsMap, stones);
    }

private:
    bool didWeGetToTheLastStone(unordered_map<int, unordered_set<int>>& jumpsMap, vector<int>& stones) {
        // if the last item is not empty means we were able to get to the last stone.
        return jumpsMap[stones[stones.size() - 1]].size() > 0;
    }
};

