func canCross(_ stones: [Int]) -> Bool {
        var queue = [[Int]]()  
        var visited = Set<[Int]>()
        var set = Set<Int>(stones)
        queue.append([0, 0])
        visited.insert([0, 0])
        while queue.count > 0 {
            var cur = queue.removeFirst()
            var position = cur[0]
            var lastJump = cur[1]

            if position == stones[stones.count - 1] {
                return true
            }

            for i in lastJump-1...lastJump+1 {
                if i < 0 {
                    continue
                }
                var right = position + i
                if set.contains(right) && !visited.contains([right, i]) {
                    queue.append([right, i])
                    visited.insert([right, i])
                }

            }
        }
        return false
    }

