class Solution(object):
    def canCross(self, stones):
        """
        :type stones: List[int]
        :rtype: bool
        """
        target = stones[-1]
        stones = set(stones)
        curunit = 1
        curspeed = 1
        memo = set()
        return self.helper(stones, curunit, curspeed, memo, target)

    def helper(self, stones, curunit, curspeed, memo, target):
        if (curunit, curspeed) in memo:
            return False

        if curunit == target:
            return True

        if curspeed <= 0 or curunit > target or curunit < 0 or curunit not in stones:
            return False

        for speed in [curspeed - 1, curspeed, curspeed + 1]:
            nextunit = curunit + speed
            if self.helper(stones, nextunit, speed, memo, target):
                return True

        memo.add((curunit, curspeed))
        return False

