            for (let k = jumpLength - 1; k <= jumpLength + 1; k++) {
                if (k > 0 && jumpMap.has(stone + k)) {
                    jumpMap.get(stone + k).add(k);
                }
            }
        }
    }

    // Check if the last stone has any valid jump lengths
    return jumpMap.get(stones[n - 1]).size > 0;
