class Solution {
    public boolean canCross(int[] a) {
            if(a[1] != 1) return false;
            if(a.length == 2) return true;
            int last_stone = a[a.length-1];
            int length = a.length;
            Map<Integer , Integer> map = new HashMap<>();
            for(int i=0;i<length;i++) map.put(a[i] , i);
            boolean dp[][] = new boolean [length][length];
            dp[0][1] = true;
            for(int i=0;i<length;i++){
                for(int j=i;j<length;j++){
                    if(dp[i][j]){
                        int jump = a[j]-a[i];
                        for(int next_step = jump-1;next_step<=jump+1;next_step++){
                            if(next_step<=0) continue;
                            int next_stone = a[j] + next_step;
                            if(a[j] != next_stone && map.containsKey(next_stone)){
                                dp[j][map.get(next_stone)] = true;
                                if(next_stone == last_stone) return true;
                            }
                        }
                    }
                }
            }
            return false;
    }
}
