```C#
public class Solution {
    private static readonly int[] _jumpAdditives = new [] { -1, 0, 1 };

    public bool CanCross(int[] stones) {
        HashSet<int> stonesSet = new HashSet<int>(stones);
        HashSet<(int Stone, int Jump)> memo = new HashSet<(int Stone, int Jump)>(); // Store the unsuccessful jump attempts.
        int end = stones[^1];

        return Solve(0, 1);

        bool Solve(int stoneVal, int jumpLength) {
            int destination = stoneVal + jumpLength;
            if (jumpLength == 0 || !stonesSet.Contains(destination)) return false;
            if (destination == end) return true;

            var memoKey = (destination, jumpLength);
            if (memo.Contains(memoKey)) return false;

            for (int i = 0; i < _jumpAdditives.Length; i++) {
                int nextJumpLength = jumpLength + _jumpAdditives[i];
                if (Solve(destination, nextJumpLength)) return true;
            }

            memo.Add(memoKey);
            return false;
        }
    }
}
