        if((stone+jump+1)>stone)   //can take jump of k+1 units
        {
            jump3=fun(mp,stone+jump+1,jump+1,dp,ls);
        }
        dp[{stone,jump}]=jump1 or jump2 or jump3;
        return dp[{stone,jump}];
    }
};
