class Solution {
public:
    map<pair<int,int>,bool> dp;
    int last;
    bool canCross(vector<int>& stones) {
        if(stones[1] != 1){
            return false;
        }
        unordered_set<int> st(stones.begin(),stones.end());
        last = stones.back();
        return cross(st,1,1);
    }
    bool cross(unordered_set<int> &st,int pos,int jump){
        if(pos == last){
            return true;
        }
        if(st.find(pos) == st.end()){
            return false;
        }
        if(dp.find({pos,jump}) != dp.end()){
            return dp[{pos,jump}];
        }
        bool x = false, y = false,z = false;
        if(jump-1 > 0){
            x = cross(st,pos+jump-1,jump-1);
        }
        if(!x){
            y = cross(st,pos+jump,jump);
        }
        if(!x and !y){
            z = cross(st,pos + jump + 1,jump+1);
        }
        return dp[{pos,jump}] = x | y | z;
    }
};

