class Solution {
public:
    bool canCross(vector<int>& stones) {
        // First jump must be 1.
        if(stones[1] - stones[0] != 1) return false;
        // Use a map since stone position is sparse.
        // Map from stone pos to a set of stone pos frog jumps from.
        unordered_map<int, unordered_set<int>> dp;
        // Init so only stones in the list is acceptable.
        for(auto s : stones) dp[s];
        // frog can jump from stones[0]->stones[1]
        dp[stones[1]].emplace(stones[0]);

        for(int i = 1; i < stones.size(); ++i){
            int stone = stones[i];
            // For each stone the frog jump from
            for(auto prev_stone : dp[stone]){
                // caculate distance k.
                int64_t k = stone - prev_stone;
                // frog can jump from stone to {stone+k-1, stone+k, stone+k+1}
                for(int64_t l = k-1; l<=k+1; ++l){
                    if(l < 0) continue;
                    int64_t m = l + stone;
                    if(m > stones.back()) continue;
                    auto it = dp.find(m); 
                    // if the frog jump into river, ignore.
                    if(it == dp.end()) continue;
                    // If the frog jump to the last stone, it succeeded.
                    if(it->first == stones.back()) return true;
                    it->second.emplace(stone);
                }
            }
        }
        return false;
    }
};
```

