	Set<Integer> set;
    int last;
    public boolean canCross(int[] stones) {
        last = stones[stones.length-1];
        set = new HashSet<>();
        for(int i:stones) {
            set.add(i);
        }
        return canCross(stones[0], 1);
    }
    private boolean canCross(int p, int speed) {
        if(!set.contains(p)) {
            return false;
        }
        if(p == last) {
            return true;
        }
        if(speed==0) {
            return false;
        }
        p+=speed;     
        return canCross(p, speed-1) || canCross(p,speed) || canCross(p,speed+1);
    }
