2. Memoization O(n^2)
```
	Set<Integer> set;
    int last;
    Map<Integer, Map<Integer,Boolean>> mem;
    public boolean canCross(int[] stones) {
        int n = stones.length;
        last = stones[n-1];
        mem = new HashMap<>();
        set = new HashSet<>();
        for(int i:stones) {
            set.add(i);
        }
        return canCross(stones[0], 1);
    }
    private boolean canCross(int p, int speed) {
        if(!set.contains(p)) {
            return false;
        }
        if(p == last) {
            return true;
        }
        if(speed==0) {
            return false;
        }
        mem.putIfAbsent(p, new HashMap<>());
        Map<Integer,Boolean>  map = mem.get(p);
        Boolean cc = map.get(speed);
        if(cc!=null) {
            return cc;
        } 
        p+=speed;     
        boolean ret = canCross(p, speed-1) || canCross(p,speed) || canCross(p,speed+1);
        map.put(speed, ret);
        return ret;
    }
