        for(int i = stones.length - 2; i >= 0; i--) {
            if (dfs(stones, i, last-stones[i])) {
                return true;
            }
        }
        return false;
    }

    public boolean dfs(int[] stones, int i, int k) {
        if(i == 0 && k == 1) {
            return true;
        }
        for(int j = i-1; j >= 0; j--) {
            if(stones[i] - stones[j] > k+1) return false;
            if(stones[i] - stones[j] < k-1) continue;
            if(dfs(stones, j, stones[i]-stones[j])) return true;
        }
        return false;
    }
}
