```
class Solution {

public:
    bool canCross(vector<int>& stones) {

        vector<vector<char>> dp(stones.size(), vector<char>(stones.size(), -1));

        return canCross(stones, dp, 0, 0);
    }

    bool canCross(vector<int>& stones, vector<vector<char>>& dp, int start=0, int jump=0) {

        if(start==stones.size()-1) 
            return true;

        if(dp[start][jump]!=-1) 
            return dp[start][jump];

        bool can=false;
        int cur=stones[start];
        for(int i=start+1; i<stones.size() && !can; i++){
            int need = stones[i]-cur;
            if(need-jump>1) 
                break;
            if(need-jump<=1 && need-jump>=-1)
                can = canCross(stones, dp, i, need);
        }

        return dp[start][jump]=can;
    }
};
```

