        // helper variables
        int res=0;
        int i=0;
        int sign=1;

        while(i<s.size()&&s[i]==' ')i++;  //ignore leading white space

        if(s[i]=='-'||s[i]=='+')          //check if number positve or negative
        {
            sign=s[i]=='-'?-1:1;
            i++;
