class Solution {

    // O(n) Solution. Basically just follow the given algorithm
    public int myAtoi(String s) {

        // Trim string to remove whitespace on the edges
        char[] charArray = s.trim().toCharArray();
        int length = charArray.length;
        boolean isNegative = false;
        int i = 0;

        // Handle first non-whitespace character. Must be a number or +/- symbol
        // Return if empty string
        if(i >= length)
            return 0;
        else if(charArray[i] == '-') {
            isNegative = true;
            i++;
        }
        else if(charArray[i] == '+') {
            i++;
        } 
        else if(!Character.isDigit(charArray[i])) { // Not a digit or +/-
            return 0;
        }   

        // Add Digits from left to right until the end of string or until a non-digit character is found
        // return zero if sum exceeds integer bounds
        Long sum = new Long(0);
        if(isNegative) {
            while(i < length && Character.isDigit(charArray[i])) {
                sum = (sum * 10L) - new Long(Character.getNumericValue(charArray[i]));

                // Check if integer will overflow
                if(sum < Integer.MIN_VALUE)
                    return Integer.MIN_VALUE;

                i++;
            }
        } 
        else {
            while(i < length && Character.isDigit(charArray[i])) {
                sum = (sum * 10L) + new Long(Character.getNumericValue(charArray[i]));

                // Check if integer will overflow
                if(sum > Integer.MAX_VALUE)
                    return Integer.MAX_VALUE;

                i++;
            }
        }

        return sum.intValue();
    }
}
