class Solution {
public:
    int myAtoi(string s) {
        int len = s.size();
        int i = 0;
        long num = 0;
        bool neg = false;

        //skip whitespaces
        while(i < len && s[i] == ' ' )
        {
            i++;
        }

        //find out if positive or negative number 
        if(i < len && s[i] == '-')
        {
            neg = true;
            i++;
        } else if(i < len && s[i] == '+')
        {
            i++;
        }

        //conversion to num
        while(s[i] >= '0' && s[i] <= '9')
        {
            num = num*10 + s[i] - '0';
            i++;

            if(num > INT_MAX)
                break;

        }

        //check if num > INT_MAX
        if(num > INT_MAX)
        {
            if(neg)
                return INT_MIN;
            else
                return INT_MAX;
        }

        if(neg)
            return (num  * -1);
        else return num;



    }
};
