        while(i<n and s[i] >= '0' and s[i] <='9'){
            int check = s[i] - '0';
            res = res*10 + check;
            i++;
        }

        if(sign == -1){
            res = -res;
        }
        res = (res > INT_MAX) ? INT_MAX : res;
        res = (res < INT_MIN) ? (INT_MIN) : res;

        return (int)res;

    }
};
