        def genTree(i,j):
            if( i > j ): return None;
            elif( i ==j ): return TreeNode(nums[i]);
            mid = (i+j)//2;
            return TreeNode(nums[mid],genTree(i,mid-1),genTree(mid+1,j) );
        return genTree(0,len(nums)-1);

