    private TreeNode BuildTree(int[] nums, int i, int j)
    {
        if (j < i)
            return null;

        int mid = j + (i - j) / 2;
        TreeNode node = new TreeNode(nums[mid]);

        node.left = BuildTree(nums, i, mid - 1);
        node.right = BuildTree(nums, mid + 1, j);

        return node;
    }
}

