```
    def sortedArrayToBST(nums: Array[Int]): TreeNode = {
      if (nums == null || nums.length == 0) return null

      val numsLen = nums.length

      def toBST(start: Int, end: Int): TreeNode = {
        if (start > end) null
        else if (start == end) new TreeNode(nums(start))
        else {
          // Set nums(mid) as value of root.
          val mid = start + ((end - start) >> 1)          
          val root = new TreeNode(nums(mid))

          root.left = toBST(start, mid - 1)
          root.right = toBST(mid + 1, end)

          root
        }
      }

      toBST(0, numsLen - 1)
    }
