```
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    TreeNode* sortedArrayToBST(vector<int>& nums) {
        destroy(pRoot_);
        pRoot_ = nullptr;
        for (auto num : nums) insert(num);
        return getRoot();
    }

private:
    TreeNode* getRoot() { return pRoot_; }

    void destroy(TreeNode *node)
    {
        if (!node)
            return;
        destroy(node->left);
        destroy(node->right);
        delete node;
    }

    size_t height(TreeNode *pNode) {
        auto const leftHeight = pNode->left ? height(pNode->left) : 0;
        auto const rightHeight = pNode->right ? height(pNode->right) : 0;
        return std::max(leftHeight, rightHeight) + 1;
    }

    ssize_t getBFactor(TreeNode *pNode)  {
        return (pNode->left ? height(pNode->left) : 0) - (pNode->right ? height(pNode->right) : 0);
    }

    void insert(int value) { pRoot_ = insert_(pRoot_, value); }

    TreeNode* rightRotate_(TreeNode *pNode) {
		auto qNode = pNode->left;
		pNode->left = qNode->right;
		qNode->right = pNode;
		return qNode;
	}

	TreeNode* leftRotate_(TreeNode *pNode) {
		auto qNode = pNode->right;
		pNode->right = qNode->left;
		qNode->left = pNode;
		return qNode;
	}

    TreeNode* balance_(TreeNode *pNode) {
		auto const bfactor = getBFactor(pNode);
		// right subtree is heavier than the left by 2, required to rotate big to the left
		if (-2 == bfactor)
		{
			// left subtree of the subtree is heavier than the right, required to rotate small to the right
			if (getBFactor(pNode->right) > 0) pNode->right = rightRotate_(pNode->right);
			pNode = leftRotate_(pNode);
		}
		// left subtree is heavier than the right by 2, required to rotate big to the right
		else if (2 == bfactor)
		{
			// right subtree of the subtree is heavier than the left, required to rotate small to the left
			if (getBFactor(pNode->left) < 0) pNode->left = leftRotate_(pNode->left);
			pNode = rightRotate_(pNode);
		}

        return pNode;
	}

    TreeNode* insert_(TreeNode *pNode, int value) {
		if (!pNode)
            return new TreeNode(value);
		else if (value < pNode->val)
			pNode->left = insert_(pNode->left, value);
		else
			pNode->right = insert_(pNode->right, value);
		return balance_(pNode);
	}

    TreeNode *pRoot_ = nullptr;
};
```

