    public TreeNode sortedArrayToBST(int[] nums, int si, int ei){
        if(si>ei){
            return null;
        }
        int mid = ei + (si-ei)/2;
        TreeNode root = new TreeNode(nums[mid]);
        root.left = sortedArrayToBST(nums,si,mid-1);
        root.right= sortedArrayToBST(nums,mid+1,ei);
        return root;
    }

