    class TreeNode{
        private:
            bool end;
            vector<TreeNode*> child;
        public:
            TreeNode(){
                this->end = false;
                child.resize(26,NULL);
            }

            void setEnd(){
                this->end = true;
            }

            void setNode(char ch){
                child[ch-'a'] = new TreeNode();
            }

            bool isEnd(){
                return this->end;
            }

            bool isSet(char ch){
                return child[ch-'a'] != NULL;
            }

