        my::TreeNode* p = root;
        for(int i = 0; i < word.size(); i++){
            char ch = word[i];
            if(p->isSet(ch)){
                p = p->getNode(ch);
            }else{
                p->setNode(ch);
                p = p->getNode(ch);
            }
        }
        p->setEnd();
