        my::TreeNode* p = root;
        for(int i = 0; i < prefix.size(); i++){
            char ch = prefix[i];
            if(p->isSet(ch)){
                p = p->getNode(ch);
            }else{
                return false;
            }
        }
        return true;
    }
};

