class Trie {
public:
    /** Initialize your data structure here. */

    // hum 26 size ka array bna rhe h kyunki, we have atmost 26 lower case alphabets
    Trie *v[26];
    // check krne ke liye ki koi particular word kha pr khatam ho rha h 
    bool isLeaf;
    // contructor
    // runs when new keyword is used to create new object of this class
    Trie() {
        //v = new Trie*[26];
        for(int i=0;i<26;i++){
            v[i] = NULL;
        }
        // starting mei leaf false, kyunki yeh koi bhi work ko represent nhi kr rha h 
        isLeaf = false;
    }

    /** Inserts a word into the trie. */
    void insert(string word) {
        // this basically refers to the root pointer of this class 
          Trie *curr = this;
        // main logic to create the trie
          for(int i=0;i<word.size();i++){
              int x = word[i]-'a';
              if(!curr->v[x]){
                  curr->v[x] = new Trie();
              }
              curr = curr->v[x];
          }
        // jab word khatam ho gya, then mark isLeaf as true, because we know yha pr word khatam ho gya h 
        curr->isLeaf = true;
    }

    /** Returns if the word is in the trie. */
    bool search(string word) {
        Trie *curr = this;
        for(int i=0;i<word.size();i++){
            int x = word[i] - 'a';
            if(!curr->v[x]){
                return false;
            }
            curr = curr->v[x];
        }
        // jahan pr hamri string khatam hui and agar vo pointer leaf bhi h, matlab vo word present h vha pr  
        if(curr->isLeaf){
            return true;
        }
        // agar vo node leaf nhi h toh word vha pr khatam nhi hua h 

        // example word inserter - apple, and we search for app, then search runs completely, but not present in the trie
        return false;

    }

    /** Returns if there is any word in the trie that starts with the given prefix. */
    bool startsWith(string word) {
         Trie *curr = this;
        for(int i=0;i<word.size();i++){
            int x = word[i] - 'a';
            // if string ka char check kr rhe h, lekin hume vo apne trie mei nhi mila, toh matlab vo prefix present nhi hogaa kabhi, so return false
            if(!curr->v[x]){
                return false;
            }
            curr = curr->v[x];
        }
        // if runs completly,  then matlab koi naa koi prefix present h, so we will retuen true;
        return true;
    }
};

