	typedef struct Trie
	{
		// Array of 26 Trie pointers. Note: The strings we insert into a trie are not stored as
		// data inside our nodes. Instead, the string that a node represents is based on the 
		// path you take to reach that node.
		struct Trie *children[26]; 
		// Used as a flag. The last node of the string we insert will be marked.
		int isWord; 
	} Trie;

	Trie* trieCreate() 
	{
		// Create and return a new Trie node.
		return calloc(1, sizeof(Trie));
	}

	void trieInsert(Trie *obj, char *word) 
	{
		int index, len = strlen(word);
		Trie *temp;

		if (obj == NULL)
			obj = trieCreate();

		// temp starts at the root node and spells out the string being inserted as it
		// moves from node to node.
		temp = obj;

		for (int i = 0; i < len; i++)
		{
			index = word[i] - 'a';

			// Before we can move forward to the next node, we need to make sure that node
			// actually exists. If not, create it.
			if (temp->children[index] == NULL)
				temp->children[index] = trieCreate();

			// Continue moving forward
			temp = temp->children[index];
		}

		// When we break out of the for-loop, temp should be at the terminal
		// node that represents the string we're trying to insert.
		temp->isWord = 1;
	}

	bool trieSearch(Trie *obj, char *word) 
	{
		if (obj == NULL)
			return false;

		Trie *temp = obj;

		// Simply iterate along the path of the word we are searching for. When we reach
		// the terminal node, if "isWord" is marked, we know the string is in the trie.
		while (*word)
		{
			temp = temp->children[*word-'a'];

			if (temp == NULL)
				return false;

			word++;
		} 

		return temp->isWord;
	}


	bool trieStartsWith(Trie *obj, char *prefix) 
	{
		if (obj == NULL)
			return false;

		Trie *temp = obj;

		// Again, we follow the path of the prefix string. Since the nodes are created 
		// as we traverse each string, we know that if the nodes exist, so must a string.
		while (*prefix)
		{
			temp = temp->children[*prefix-'a'];

			if (temp == NULL)
				return false;

			prefix++;
		} 

		return true;
	}
	// Recursively free all paths through our Trie.
	void trieFree(Trie* obj) 
	{
		for (int i = 0; i < 26; i++)
			if (obj->children[i])
				trieFree(obj->children[i]);

		free(obj);
	}

