final class Trie {
    val chars = collection.mutable.Map.empty[Char, Trie]
    var term = false

    def insert(word: String, i: Int = 0): Unit = 
        if (i < word.length) 
            chars.getOrElseUpdate(word(i), new Trie).insert(word, i + 1)  
        else term = true

    def search(word: String, prefix: Boolean = false , i: Int = 0): Boolean = 
        if (i < word.length)
            chars.get(word(i)).exists(_.search(word, prefix, i + 1))
        else prefix || term

    def startsWith(prefix: String): Boolean = search(prefix, true)
}
```

