    }

    void insert(string word) {
        int now = 0;
        for(int i=0; i < word.size(); i++) {
            int id = word[i] - 'a';
            if(nxt[now][id] == 0) {
                node++;
                nxt[now][id] = node;
            }
            now = nxt[now][id];
        }
        mark[now]=true;
    }

    bool search(string word) {
        int now = 0;
        for(int i=0; i < word.size(); i++) {
            int id = word[i]-'a';
            if(nxt[now][id] == 0) return false;
            now = nxt[now][id];
        }
        return mark[now];
