    public boolean search(String word) {
        if(word == null){
            return false;
        }
        TrieNode temp = root;
        for(int i = 0; i < word.length(); i ++){
            char c = word.charAt(i);
            if(temp.next[c - 97] == null){
                return false;
            }
            else{
                temp = temp.next[c - 97];
            }
