class Solution:
    def splitArray(self, nums: List[int], m: int) -> int:
        def dfs(i, cntSubarrays, curSum, curMax):
            if i == n and cntSubarrays == m:
                self.ans = min(self.ans, curMax)
                return 
            if i == n:
                return
            if i > 0:
                dfs(i + 1, cntSubarrays, curSum + nums[i], max(curMax, curSum + nums[i]))
            if cntSubarrays < m:
                dfs(i + 1, cntSubarrays + 1, nums[i], max(curMax, nums[i]))
        n = len(nums)
        self.ans = float('inf')
        dfs(0, 0, 0, 0)
        return self.ans

