class Solution:
    def splitArray(self, nums: List[int], m: int) -> int:
        n = len(nums)
        '''
        dp[i][j] is the minimum largest subarray sum for splitting nums[0..i] into j parts.
        '''
        dp = [[float('inf') for _ in range(m + 1)] for _ in range(n + 1)]
        dp[0][0] = 0
        for i in range(1, n + 1):
            for j in range(1, m + 1):
                for k in range(i):
                    '''
                    dp[i][j] can be derived from max(f[k][j - 1], nums[k + 1] + ... + nums[i]).
                    For all valid index k, 
                    f[i][j] should choose the minimum value of the above formula.
                    '''
                    dp[i][j] = min(dp[i][j], max(dp[k][j-1], sum(nums[k:i]))) # here i == i + 1in nums
        return dp[n][m]       
