class Solution:
    def splitArray(self, nums: List[int], m: int) -> int:
        n = len(nums)
        presum = [0] * (n + 1)
        for i in range(n):
            presum[i + 1] = presum[i] + nums[i] 
        dp = list(presum)
        for j in range(1, m): # we do m - 1 splits on num[j + 1:n]
            for i in range(n, j, -1): # find where dp[k] = sum(nums[k:i]).
                # k ranges from j to i - 1
                l, r = j, i - 1
                while l <= r:
                    m = l + (r - l) // 2
                    if dp[m] >= presum[i] - presum[m]:
                        r = m - 1
                    else:
                        l = m + 1
                    dp[i] = min(max(dp[r], presum[i] - presum[r]), max(dp[l], presum[i] - presum[l]))
        return dp[-1]

