* ***Using Binary Search***

* ***Time Complexity :- O(N * log(total_sum - max. element))***

* ***Space Complexity :- O(1)***

```
class Solution {
public:

    // this function check that is it possible to take max. sum od subarray == mid

    bool is_possible(vector<int>& arr, int mid, int k)
    {
        int n = arr.size();

        // calculate no. of possible partition

        int count = 1;

        int curr_sum = 0;

        for(int i = 0; i < n; i++)
        {
            if(curr_sum + arr[i] <= mid)
            {
                curr_sum += arr[i];
            }

            // if curr_sum exceed the mid, then increment the no. of partition and update the curr_sum

            else
            {
                count++;

                curr_sum = arr[i];
            }
        }

        return count <= k;
    }

    int splitArray(vector<int>& arr, int k) {

        // apply binary search to get minimum

        // lowest max. sum will be the max. of array

        int low = *max_element(arr.begin(), arr.end());

        // highest max sum will be the sum of whole array

        int high = 0;

        for(int i = 0; i < arr.size(); i++)
        {
            high += arr[i];
        }

        int mini = -1;

        while(low <= high)
        {
            // find mid

            int mid = low + (high - low) / 2;

            // if the no. of required partition is <= k

            if(is_possible(arr, mid, k))
            {
                // update mini

                mini = mid;

                high = mid - 1;
            }

            // if no. of required partition is more than k

            else
            {
                low = mid + 1;
            }
        }

        return mini;
    }
};
