        int high = 0;

        for(int i = 0; i < arr.size(); i++)
        {
            high += arr[i];
        }

        int mini = -1;

        while(low <= high)
        {
            // find mid

            int mid = low + (high - low) / 2;

            // if the no. of required partition is <= k

            if(is_possible(arr, mid, k))
            {
                // update mini

                mini = mid;

                high = mid - 1;
            }

            // if no. of required partition is more than k

            else
            {
                low = mid + 1;
            }
        }

        return mini;
    }
};
