class Solution {
private:
    bool isPossible(vector<int>& nums, int val, int m){
        int curSum = 0, subArrays = 1;
        for(int &i : nums){
            if(curSum + i > val){ subArrays++; curSum = 0;}
            curSum += i;
        }
        return subArrays <= m;
    }
public:
    int splitArray(vector<int>& nums, int m) {
        int l = *max_element(nums.begin(), nums.end()); 
        int r = accumulate(nums.begin(), nums.end(), 0);
        int ans = 0;
        while(l <= r){
            int mid = l + ((r-l)>>1);
            if(isPossible(nums, mid, m)) r = mid - 1;
            else l = mid + 1;
        }
        return l;
    }
};
