class Solution {
public:


    vector<int>pre;
    int n;
    int check(int mid)
    {
        int seg = 0, prev = -1;
        for (int i = 0; i < n; i++)
        {
            if (prev == -1 and pre[i] > mid)
            {
                seg++;
                prev = pre[i - 1];
            }
            if (prev != -1 and pre[i] - prev > mid)
            {
                seg++;
                prev = pre[i - 1];
            }
        }
        seg++;
        return seg;
    }

    int splitArray(vector<int>& arr, int k) {
        n = arr.size();
        pre.resize(n);
        pre[0] = arr[0];
        for (int i = 1; i < n; i++)
            pre[i] = pre[i - 1] + arr[i];
        int lo = *max_element(arr.begin(),arr.end()), hi = pre.back(), ans = pre.back();
        while (lo <= hi)
        {
            int mid = (lo + hi) / 2;
            if (check(mid)>k)
                lo=mid+1;
            else
                hi= mid - 1,ans=mid;
        }
        return ans;
    }
};
```

