class Solution {
    public int splitArray(int[] nums, int k) {
        int low = IntStream.of(nums).max().getAsInt(); //Minimum sum of the subarray
        int high = Arrays.stream(nums).sum(); //Maximum sum of the subarray
        int min = Integer.MAX_VALUE; //To keep track of our answer.
        while(low <= high) {
            int mid = (low + high)/2; //Current sum of subarray
            if(isValid(mid,nums,k)) { //If subarray sum is valid i.e., it can be used to divide array into K subarrays.
                min = Math.min(min,mid);
                high = mid - 1; //Find a smaller subarray
            }
            else low = mid + 1; //We didn't find a subarray large enough to split array into K parts.
        }
        return min; //return answer
    }
    private boolean isValid(int mid,int[] nums,int k) {
        int count = 0; //No of subarrays that'll be generated for current sum
        int low = 0;
        while(low < nums.length) {
            int sum = 0;
            while(low < nums.length && sum + nums[low] <= mid) sum += nums[low++]; 
            count++; //Increment in no. of subarrays when one subarray is completed.
        }
        return count <= k; //If no. of subarrays are in the range of K return true;
    }
}
