class Solution {
    public int splitArray(int[] nums, int m) {

        return recursive(nums, m, 0);
    }

    private int recursive(int[] nums, int m, int index) {
        if(m==1){
            int sum=0;
            for(int i=index; i<nums.length; i++)
                sum += nums[i];
            return sum;
        }
        int min = Integer.MAX_VALUE;
        int sum=0;
        for(int i=index; i<=nums.length-m; i++) {
            sum += nums[i];
            min = Math.min(min, Math.max(sum, recursive(nums, m-1, i+1)));
        }
        return min;
    }
}
