class Solution {
    public int splitArray(int[] nums, int m) {
        int[][] memo = new int[m+1][nums.length];
        for(int i=0; i<=m; i++)
          Arrays.fill(memo[i], -1);
        return recursive(nums, m, 0, memo);
    }

    private int recursive(int[] nums, int m, int index, int[][] memo) {
        if(m==1){
            int sum=0;
            for(int i=index; i<nums.length; i++)
                sum += nums[i];
            return sum;
        }
        if(memo[m][index]!=-1)
            return memo[m][index];

        int min = Integer.MAX_VALUE;
        int sum=0;
        for(int i=index; i<=nums.length-m; i++) {
            sum += nums[i];
            min = Math.min(min, Math.max(sum, recursive(nums, m-1, i+1, memo)));
        }
        return memo[m][index]=min;
    }
}
