class Solution {
    public int splitArray(int[] nums, int k) {

        int l = 0,r = 0,m;

        for(int i = 0; i< nums.length;i++){
            l = Math.max(l,nums[i]);
            r+=nums[i];
        }

        while(l<r){
            m=(l+r)/2;
            if(canBeSplitted(m, k, nums)){
                r=m;
            }else{
                l=m+1;
            }
        }
        return l;
