    public boolean canBeSplitted(int m, int k, int[] nums){
        int count = 1;
        int sum = 0;
        for(int i=0;i<nums.length;i++){
            if(sum+nums[i]>m){
                count++;
                sum=0;
            }
            sum+=nums[i];
        }
        return count<=k;
