# Code
```
class Solution {
public:

    int ans = INT_MAX;
    vector<vector<int>> dp;

    int solve(vector<int> &nums, int k, int st)
    {
        //base conditions
        if(k == 0 and st<nums.size()) return -1;

        if(st==nums.size() and k > 0) return -1;

        if(k==0 and st == nums.size()) return 0;

        if(dp[st][k] != -1) return dp[st][k];

        int sum = 0;
        int ans = INT_MAX;

        //i have to return the minimum of max subarray sums i get from each type of partition

        for(int i =st; i<nums.size(); i++)
        {
            sum += nums[i];

            //supppose i put a partition here
            int temp = solve(nums,k-1,i+1);

            //for every partition made i calc the possible ans
            if(temp != -1) //checking if the partition was valid
            {
                temp = max(temp,sum);
                ans = min(ans,temp);
            }
        }

        return dp[st][k] = ans;
    }


    int splitArray(vector<int>& nums, int k) {

        dp.resize(nums.size()+1, vector<int> (k+1,-1));

        //i will have to follow recursive partitioning with taking wiht me the sum of subarray as it should be minimized

        return solve(nums,k,0);

        // return ans;

    }
};
```

