class Solution {
    public int splitArray(int[] nums, int k) {

        int left = 0;
        int right = 0;
        int totalSum = 0, maxSum = 0;

        for (int x : nums) {
            totalSum += x;
            maxSum = Math.max(x, maxSum);
        }

        left = maxSum;
        right = totalSum;

        while (left < right) {
            int mid = left + (right - left) / 2;
            if (f (nums, mid, k)) {
                right = mid;
            } else {
                left = mid + 1;
            }
        }
        return right;
    }
    static boolean f (int[] nums, int target, int k) {

        int maxChunks = 1;
        int sum = 0;

        for (int i = 0; i < nums.length; i++) {
            sum += nums[i];
            if (sum > target) {
                sum = nums[i];
                maxChunks++;
            }
        }
        return maxChunks <= k;
    }
}
