    long long int findPage(vector<int>& nums, vector<long long>&prefix, vector<vector<long long>>& dp, int n, int l, int N, int M){
        if(dp[l][M-1]!=-1) return dp[l][M-1];
        if(M==1){
            return prefix[n]-prefix[l];
        }
        long long int ret=INT_MAX;
        for(int i=l; i<=n-M; i++){
             ret = min(ret, max(prefix[i+1]-prefix[l], findPage(nums, prefix, dp, n, i+1, N-i-1, M-1)));
        }
        dp[l][M-1] = ret;
        return ret;
    }
