    {
        int N = nums.size();
        vector<long long int> prefix(N+1);
        prefix[0]=0;
        for(int i=1; i<=N; i++) prefix[i] = nums[i-1]+prefix[i-1];
        if(M==1){
            return prefix[N];
        }
        if(M>N) return -1;
        if(M==N) {
            int maxi=INT_MIN;
            for(auto i:nums)
            maxi = max(maxi, i);
            return maxi;
        }
        vector<vector<long long>> dp(N, vector<long long>(M,-1));
        long long int ret=INT_MAX;

        for(int i=0; i<=N-M; i++){
            ret = min(ret, max(prefix[i+1], findPage(nums, prefix, dp, N, i+1, N-i-1, M-1)));
        }
        return ret;
    }
