class Solution {
public:    
    bool check(vector<int>& nums, int mid, int m) {
        int count = 0, sum = 0;
        for(int num : nums) {
            if(num > mid)
                return false;
            sum += num;
            if(sum > mid) {
                count++;
                sum = num;
            }
        }
        count++;

        return count <= m;
