class Solution {
    public int splitArray(int[] nums, int m) {
        int min = Arrays.stream(nums).max().getAsInt(); // min possible result is the max num of the array.
        int max = Arrays.stream(nums).sum(); // max possible result is the sum of the whole array.

        while (min < max) { // binary search the min value between [min - max] range.
            int mid = min + (max - min) / 2;
            int count = split(nums, mid); // put this value to see the count of the splitted array

            if (count <= m) { 
                max = mid; // set max boundary to try left range.
            } else {
                min = mid + 1; // set min boundary to try right range.
            }
        }
        return min; // min is the "min" of the largest sum.
    }


    // get the count of subarrays with given max sum limit
    int split(int[] nums, int maxSum) {
        int count = 1;
        int sum = 0;
        for (int i = 0; i < nums.length; i++) {
            if (sum + nums[i] > maxSum) {
                count++;
                sum = nums[i];
            } else {
                sum = sum + nums[i];
            }
        }
        return count;
    }
}
