class Solution:
    def splitArray(self, nums: List[int], m: int) -> int:
        def cond(mid, m):
            capacity = -1
            for num in nums:  # becasue it's splitting continuous sub-arrays, we can do it this way
                if num <= capacity:
                    capacity -= num
                else:
                    capacity = mid - num
                    m -= 1
            return m >= 0

        l, r = max(nums), sum(nums)
        while l < r:
            mid = (l + r) // 2
            if cond(mid, m):
                r = mid
            else:
                l = mid + 1
        return l
